import Phaser from 'phaser';

class MyGame extends Phaser.Scene {
    constructor() {
        super();
    }

    preload() {
        this.load.spritesheet('crash', 'node_modules/Crashntrancesheet1.gif', {
            frameWidth: 32,
            frameHeight: 32
        })
    }

    create() {
        this.anims.create({
            key: 'walk',
            frames: this.anims.generateFrameNumbers('crash', {
                start: 0,
                end: 3
            }),
            frameRate: 10,
            repeat: -1
        });
        this.player = this.add.sprite(100, 100, 'crash');
    }
}

const config = {
    type: Phaser.AUTO,
    width: 800,
    height: 600,
    scene: MyGame
};

const game = new Phaser.Game(config);